   // We have new updates that haven't been processed yet.\n    // We'll add them to the base queue.\n    if (baseQueue !== null) {\n      // Merge the pending queue and the base queue.\n      const baseFirst = baseQueue.next\n      const pendingFirst = pendingQueue.next\n      baseQueue.next = pendingFirst\n      pendingQueue.next = baseFirst\n    }\n\n    current.baseQueue = baseQueue = pendingQueue\n    queue.pending = null\n  }\n\n  // Calculate Hook state.\n  if (baseQueue !== null) {\n    // We have a queue to process.\n    const first = baseQueue.next\n    let newState = current.baseState\n\n    let newBaseState = null\n    let newBaseQueueFirst = null\n    let newBaseQueueLast = null\n    let update = first\n\n    do {\n      const updateLane = update.lane\n\n      if (!isSubsetOfLanes(renderLanes, updateLane)) {\n        // \u4F18\u5148\u7EA7\u4E0D\u591F: \u52A0\u5165\u5230 baseQueue, \u7B49\u5F85\u4E0B\u4E00\u6B21 render.\n        const clone: Update<S, A> = {\n          lane: updateLane,\n          action: update.action,\n          hasEagerState: update.hasEagerState,\n          eagerState: update.eagerState,\n          next: null,\n        }\n\n        if (newBaseQueueLast === null) {\n          newBaseQueueFirst = newBaseQueueLast = clone\n          newBaseState = newState\n        } else {\n          newBaseQueueLast = newBaseQueueLast.next = clone\n        }\n\n        // Update the remaining priority in the queue.\n        currentlyRenderingFiber.lanes = mergeLanes(\n          currentlyRenderingFiber.lanes,\n          updateLane\n        )\n        markSkippedUpdateLanes(updateLane)\n      } else {\n        // This update does have sufficient priority (\u4F18\u5148\u7EA7\u8DB3\u591F).\n        // Merge state.\n        if (newBaseQueueLast !== null) {\n          // Update baseQueue\n          const clone: Update<S, A> = {\n            lane: NoLane,\n            action: update.action,\n            hasEagerState: update.hasEagerState,\n            eagerState: update.eagerState,\n            next: null,\n          }\n          newBaseQueueLast = newBaseQueueLast.next = clone\n        }\n\n        // Process this update.\n        if (update.hasEagerState) {\n          // \u6027\u80FD\u4F18\u5316:\n          // If this update is a state update (not a reducer) and was processed eagerly,\n          // we can use the eagerly computed state\n          newState = update.eagerState\n        } else {\n          // \u8C03\u7528 Reducer \u83B7\u53D6\u6700\u65B0\u72B6\u6001.\n          const action = update.action\n          newState = reducer(newState, action)\n        }\n      }\n\n      update = update.next\n    } while (update !== null && update !== first)\n\n    if (newBaseQueueLast === null)\n      newBaseState = newState\n    else newBaseQueueLast.next = newBaseQueueFirst\n\n    // Mark that the fiber performed work,\n    // but only if the new state is different from the current state.\n    if (!is(newState, hook.memoizedState))\n      markWorkInProgressReceivedUpdate()\n\n    // \u628A\u8BA1\u7B97\u540E\u7ED3\u679C\u66F4\u65B0\u5230 workInProgressHook.\n    hook.memoizedState = newState\n    hook.baseState = newBaseState\n    hook.baseQueue = newBaseQueueLast\n    queue.lastRenderedState = newState\n  }\n\n  // Return Hook state and dispatch action.\n  const dispatch: Dispatch<A> = queue.dispatch\n  return [hook.memoizedState, dispatch]\n}\n"})}),"\n",(0,s.jsx)(n.h2,{id:"usereducer",children:"useReducer"}),"\n",(0,s.jsxs)(n.p,{children:["Use ",(0,s.jsx)(n.code,{children:"useState"})," if:"]}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"manage JavaScript primitives as state"}),"\n",(0,s.jsx)(n.li,{children:"have simple state transitions"}),"\n",(0,s.jsx)(n.li,{children:"want to have business logic within components"}),"\n",(0,s.jsx)(n.li,{children:"have different properties that don\u2019t change in any correlated manner\nand can be managed by multiple useState hooks"}),"\n",(0,s.jsx)(n.li,{children: